import React, { useEffect, useState } from "react";
import api from "../api/api";
import ProspectList from "./ProspectList";
import {
  Box,
  Typography,
  Paper,
  CircularProgress,
  Divider,
} from "@mui/material";

export default function Dashboard({ campaignId }) {
  const [dashboard, setDashboard] = useState(null);
  useEffect(() => {
    api.getDashboard(campaignId).then(setDashboard);
  }, [campaignId]);
  if (!dashboard)
    return (
      <Box sx={{ display: "flex", justifyContent: "center", mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  return (
    <Paper elevation={3} sx={{ p: 3, maxWidth: 700, margin: "2rem auto" }}>
      <Typography variant="h5" gutterBottom>
        Campaign Dashboard
      </Typography>
      <Divider sx={{ mb: 2 }} />
      <Box sx={{ display: "flex", gap: 4, mb: 2 }}>
        <Box>
          <Typography variant="subtitle1">Connections</Typography>
          <Typography variant="h6">{dashboard.connections}</Typography>
        </Box>
        <Box>
          <Typography variant="subtitle1">Replies</Typography>
          <Typography variant="h6">{dashboard.replies}</Typography>
        </Box>
        <Box>
          <Typography variant="subtitle1">ROI</Typography>
          <Typography variant="h6">{dashboard.roi}</Typography>
        </Box>
      </Box>
      <Divider sx={{ mb: 2 }} />
      <ProspectList prospects={dashboard.prospects || []} />
    </Paper>
  );
}
